<?php	session_start();	$_SESSION['players'] = array(20, 6);	$_SESSION['courseID'] = 1;		$root = realpath($_SERVER["DOCUMENT_ROOT"]) . "/";	require_once $root . 'model/scorecard.php';	require_once $root . 'model/course.php';	require_once $root . 'db/DBAdapter.php';		$dbAdapter = new DBAdapter(null, null);	$myScoreCard = new scorecard(		$dbAdapter,		new course($dbAdapter, $_SESSION['courseID']),		$_SESSION['players'],		"9/4/2011"	);		$playersInOrder = "";	$players = "";	for ($a = 0; isset($myScoreCard->playersInOrder[$a]); $a++) {		$curPlayer = $myScoreCard->playersInOrder[$a];		$playersInOrder .= $curPlayer . ", ";		$players .= $myScoreCard->players[$curPlayer]->playerName . ", ";	}		echo "myScoreCard:<br />\n";	echo "playersInOrder = " . $playersInOrder . "<br />\n";	echo "players = " . $players . "<br />\n";	echo "lastRecordedHole = " . $myScoreCard->lastRecordedHole . "<br />\n";	echo "nextSelectedHole = " . $myScoreCard->nextSelectedHole . "<br />\n";	echo "<br /><br />\n";		echo "myScoreCard.players:<br />\n";	foreach ($myScoreCard->players as $a => $b) {		echo $a . " = " . $b->playerName . "<br />\n";		foreach ($b as $c => $d) {			echo "&nbsp;&nbsp;&nbsp;&nbsp;" . $c . " = ";			if (is_array($d)) {				echo implode('**', $d);			} else {				echo $d;			}			echo "<br />\n";		}		echo "<br />\n";	} /**/	session_destroy();?>